mapper.insert:



            SELECT IFNULL(MAX(FILE_NO), 0) + 1 
            FROM TB_RECRUIT_BOARD_FILE
            WHERE BOARD_NO = #{board_no}



		    INSERT 
		    INTO
		    TB_RECRUIT_BOARD_FILE 
		    (
		    	board_no,
		    	file_no,
		    	file_name_key,
		    	file_name,
		    	file_path,
		    	file_size,
		    	reg_date
		    )
		     VALUES 
		     (
		     	  #{board_no}
	            , #{file_no}
	            , #{file_name_key}
	            , #{file_name}
	            , #{file_path}
	            , #{file_size}
			     , NOW()
			  )

mapper.delete:

UPDATE tb_recruit_board
   SET BOARD_TITLE = #{board_title}
     , BOARD_CONTENT = #{board_content}
     , REG_DATE = NOW()
 WHERE BOARD_NO = #{board_no}

